// src/routes/placeholder/placeholder.handler.ts
import type { Context, TypedResponse } from "hono";

import { placeholderQuerySchema } from "@/lib/placeholder-schema";
import { hexToRgba } from "@/utils/color-utils";

// Base64-encoded font (replace with actual Base64 string)
const customFontBase64 = `data:font/ttf;base64,`;

// Helper function to generate the SVG placeholder
function generateSvg({ width, height, text, fontSize, bgColor, fontColor }: { width: number; height: number; text: string; fontSize: number; bgColor: string; fontColor: string }) {
  return `<?xml version="1.0" encoding="UTF-8"?>
<svg width="${width}" height="${height}" xmlns="http://www.w3.org/2000/svg">
  <defs>
    <style type="text/css">
      @font-face {
        font-family: 'CustomFont';
        src: url('${customFontBase64}') format('truetype');
      }
    </style>
  </defs>
  <rect width="100%" height="100%" fill="${bgColor}" />
  <text x="50%" y="50%" text-anchor="middle" alignment-baseline="middle" font-family="'CustomFont'" font-size="${fontSize}" fill="${fontColor}">
    ${text}
  </text>
</svg>`;
}

// Handler for the placeholder route
export async function placeholderHandler(c: Context) {
  const { width = 600, height = 400, text = `${width} x ${height}`, font_size = "0", bg_color = "EEEEEEFF", font_color = "31343DFF" } = c.req.query();

  const widthInt = Number.parseInt(width as string);
  const heightInt = Number.parseInt(height as string);
  const fontSize = font_size === "0" ? widthInt / Math.round((text as string).length / 2) : Number.parseInt(font_size as string);
  const bgColor = hexToRgba(bg_color as string);
  const fontColor = hexToRgba(font_color as string);

  // Dynamically adjust font size if it's not provided
  let finalFontSize = fontSize;
  if (fontSize === 0) {
    while (finalFontSize > 10 && (text as string).length * finalFontSize > widthInt - 20) {
      finalFontSize--;
    }
  }

  const svg = generateSvg({
    width: widthInt,
    height: heightInt,
    text: text as string,
    fontSize: finalFontSize,
    bgColor,
    fontColor,
  });

  c.header("Content-Type", "image/svg+xml");

  return new Response(svg, {
    headers: { "Content-Type": "image/svg+xml" },
  }) as unknown as TypedResponse<{ message: string }, 200, "json">;
}
